use ossim;
SET AUTOCOMMIT=0;
BEGIN;


DROP TABLE IF EXISTS host_apps;
CREATE TABLE IF NOT EXISTS host_apps (
  ip INT( 10 ) UNSIGNED NOT NULL ,
  app TEXT NOT NULL ,
  KEY `ip` (`ip`)
);

ALTER TABLE `wireless_sensors` ADD `avg_signal` INT( 10 ) NOT NULL ;
ALTER TABLE `acl_templates` CHANGE `allowed_nets` `allowed_nets` TEXT;
ALTER TABLE `acl_templates` CHANGE `allowed_sensors` `allowed_sensors` TEXT;
ALTER TABLE `users` CHANGE `allowed_sensors` `allowed_sensors` TEXT NOT NULL;
ALTER TABLE `users` CHANGE `allowed_nets` `allowed_nets` TEXT DEFAULT '' NOT NULL;
ALTER TABLE plugin_sid ADD subcategory_id INT;
ALTER TABLE plugin ADD source_type text;
ALTER TABLE plugin ADD vendor text;
ALTER TABLE `alarm` ADD `uuid_event` TEXT;
ALTER TABLE `alarm` ADD `uuid_backlog` TEXT;
ALTER TABLE `event` ADD `uuid` TEXT;
ALTER TABLE `backlog` ADD  `uuid` TEXT;
ALTER TABLE `backlog_event` ADD `uuid` TEXT;
ALTER TABLE `backlog_event` ADD  `uuid_event` TEXT;
ALTER TABLE `action_exec` CHANGE `command` `command` TEXT NOT NULL;


DROP TABLE IF EXISTS `category`;
CREATE TABLE IF NOT EXISTS `category` (
  `id` int(11) NOT NULL,
  `name` varchar(100) NOT NULL,
  PRIMARY KEY (`id`)
);

DROP TABLE IF EXISTS `subcategory`;
CREATE TABLE IF NOT EXISTS `subcategory` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `cat_id` int(11) NOT NULL,
  `name` text,
  PRIMARY KEY (`id`)
);

DROP TABLE IF EXISTS `custom_report_profiles`;
CREATE TABLE IF NOT EXISTS `custom_report_profiles` (
`name` varchar(64) NOT NULL,
`header` varchar(64) NOT NULL,
`lfooter` varchar(64) NOT NULL,
`rfooter` varchar(64) NOT NULL,
`color1` varchar(64) NOT NULL,
`color2` varchar(64) NOT NULL,
`color3` varchar(64) NOT NULL,
`color4` varchar(64) NOT NULL,
PRIMARY KEY (`name`)
) ;

DROP TABLE IF EXISTS `custom_report_types`;
CREATE TABLE IF NOT EXISTS `custom_report_types` (
`id` int(11) NOT NULL,
`name` varchar(128) NOT NULL,
`type` varchar(128) NOT NULL,
`file` varchar(128) NOT NULL,
`inputs` text NOT NULL,
`sql` text NOT NULL,
`dr` int(11) NOT NULL default '1',
PRIMARY KEY (`id`)
);


-- Predefined reports



DROP PROCEDURE IF EXISTS addsindex;
DELIMITER '//'
CREATE PROCEDURE addsindex() BEGIN
  IF EXISTS
      (SELECT * FROM INFORMATION_SCHEMA.STATISTICS WHERE TABLE_NAME = 'alarm' AND INDEX_NAME='status')
  THEN
      ALTER TABLE `alarm` DROP INDEX `status`;
  END IF;
END;
//
DELIMITER ';'
CALL addsindex();
DROP PROCEDURE addsindex;
ALTER TABLE `alarm` ADD INDEX `status` ( `status` , `timestamp` );


